    var message;

    this.tokenvalid.set(undefined);

    if (!this.token.get()) {
      return;
    }

    message = this.message('/');

    message.onreceive = (function() {
      this.tokenvalid.set(true);
      this.emit('login');
    }).bind(this);

    message.onerror = (function() {
      this.tokenvalid.set(false);
      this.emit('error');
    }).bind(this);

