    this.toast = undefined;
    this.initToast(active);

    this.clickControllers = this.views.map(function(view) {
      var options;

      options = {
        active: active,
        event: 'mousedown',
        callbackthis: active
      };

      return new ListClickController(view,
          TeamRemoveController.removalCallback, options);
    });

