      listener = {
        updatecount: 0,
        /**
         * Callback listener
         */
        onupdate: function() {
          listener.updatecount += 1;
        },
        /**
         * counter reset
         */
        reset: function() {
          listener.updatecount = 0;
        },
        emitters: []
      };

      model = new PlayerModel();
      model.registerListener(listener);

      ref = PlayerModel.NONAME;
      res = model.getName();
      QUnit.equal(res, ref, 'initialization without argument');

      model.setName('');
      ref = PlayerModel.NONAME;
      res = model.getName();
